import React, { Component } from 'react'
import { withRouter } from 'react-router-dom';
import { Menu, Segment } from 'semantic-ui-react'

class NavBar extends Component {
  handleSignout = () => {
    this.props.history.push('/')
    localStorage.clear()
 } 

  render() {
    return (
      <div>
       <Segment inverted>
        <Menu inverted pointing secondary>
          <Menu.Item
            name='home'
            active={this.props.location.pathname==='/home'}
            onClick={() => this.props.history.push('/home')}/>
          {/* <Menu.Item
            name='favorite'
            active={this.props.location.pathname === '/favorite'}
            onClick={() => this.props.history.push('/favorite')}/> */}
          <Menu.Item
            name='signout'
            onClick={this.handleSignout}
          />
        </Menu>
      </Segment>
      </div>
    )
  }
}
const mapStatetoProps = state => {
  return ({
    favorites: state.favorites
  })
}

export default withRouter(connect(mapStatetoProps, { landMovies, toggleFilter, setUserFavorites, toggleFavFilter, setFilteredFavs })(NavBar));
