import React, { Component } from 'react'
import { Card } from 'semantic-ui-react';
import MovieCard from './MovieCard'
import { connect } from 'react-redux';


class MovieContainer extends Component {

  constructor() {
    super() 

    this.state = {
       start: 0
    }
 }

 componentDidMount() {
    window.addEventListener('scroll', this.onScroll, false);
 }

 componentWillUnmount() {
    window.removeEventListener('scroll', this.onScroll, false);
 }

 onScroll = () => {
    if ((window.innerHeight + window.scrollY) >= (document.body.offsetHeight) && !this.props.landingMovies.isLoading) 
       {this.onPaginatedSearch()}
 } 

 onPaginatedSearch = () => {
       this.setState({
          start: this.state.start + 56
       })
       if (this.props.filter === 'all') {
          return getMoreMovies(this.state.start)
          .then((data) => {this.props.landMoreMovies(data.results)})
       } else {
          return getMoreFilteredMovies(this.state.start, this.props.filter)
          .then((data) => {this.props.landMoreMovies(data.results)})
       }
    }


  render() {
    const eachMovie = this.props.Movies.map((movie) => 
    <MovieCard 
       key={movie.id}
       movieId={movie.id}
       img={movie['poster_240x342']} />)

    return(
       <Card.Group itemsPerRow={7}>
          {eachMovie}
       </Card.Group>
    )
  }
} 

const mapStatetoProps = state => {
  return ({
    Movies: state.Movies
  })
}

export default connect(mapStatetoProps)(MovieContainer)
