import React, { Component } from 'react'
import { withRouter } from 'react-router-dom';
import { Menu, Input, Segment } from 'semantic-ui-react'

class NavBar extends Component {
  state = {
    showModal: false,

    query: null,

    page: 1,
    lastPage: 1,

  	results: null,
    total_results: null
  }

  searchTMDB = (pageNum=1, query=this.state.query ) => {
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=${tmdb_key}&query=${query}&language=en-US&page=${pageNum}&include_adult=false`)
    .then(res => res.json())
    .then(res => {
      this.setState({ 
        query: query,
        page: res.page,
        lastPage: res.total_pages,
        results: res.results,
        total_results: res.total_results
      })
    })
  }

  handleSignout = () => {
    this.props.history.push('/')
    localStorage.clear()
  } 
  handleSubmit = e => {
    e.preventDefault()
    this.searchTMDB(1, e.target.query.value)
  }

  render() {
    let searchBar
    if (this.props.location.pathname === '/home') {
      searchBar = <Input icon='search' placeholder='SEARCH MOVIES' onChange={this.props.handleSearch} />
    }

    return (
      <div>
       <Segment inverted>
        <Menu inverted pointing secondary>
          <Menu.Item
            name='home'
            active={this.props.location.pathname==='/home'}
            onClick={() => this.props.history.push('/home')}/>
          {/* <Menu.Item
            name='favorite'
            active={this.props.location.pathname === '/favorite'}
            onClick={() => this.props.history.push('/favorite')}/> */}
          <Menu.Menu position='right'>
            <Menu.Item>
              {searchBar}
            </Menu.Item>
            <Menu.Item
              name='SIGN-OUT'
              onClick={this.handleSignout}/>
          </Menu.Menu>
        </Menu>
      </Segment>
      </div>
    )
  }
}

export default withRouter(NavBar);
